/* The four adjacent digits in the 1000-digit number that have the greatest product are 9 × 9 × 8 × 9 = 5832. */
/* Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product? */

#define NUMBER 

/* Number of sucessfull digits */
#define SUCCD 13

#include <stdio.h>

/* Returns the sum of the passed array */
void sum_array(long int*, char*);
/* Created the buffer to sum */
void create_buffer(char*, char*);

/* Main Function */
int main(){

    /* The big number */
    char* str = "";

    /* Holds maximum value found */
  long int max=0;

  /* Creates array with the numbers to sum */
  char buffer[SUCCD];

  while(*str != '\0'){

    create_buffer(str, buffer);
    /* printf("buffer: %s\n", buffer); */

    sum_array(&max, buffer);

    str++;
  }
  printf("max: %ld\n", max);

}

void create_buffer(char* s, char* buff){

  for(int i=0; i<SUCCD; i++){
    *buff++ = *s++;
  }
}

void sum_array(long int* m, char* s){

  long int v=1, j=0;

  while(j++ < SUCCD){
    
    v *= *s++ - '0' ;
  }

  /* printf("v: %d\n", v); */
  if(v > *m){*m = v;}
}
